BEGIN
  EXECUTE IMMEDIATE 'DROP TABLE CLIENTS';
  EXCEPTION WHEN OTHERS THEN NULL;
END;
/
BEGIN
  EXECUTE IMMEDIATE 'DROP TABLE TRANSACTIONS';
  EXCEPTION WHEN OTHERS THEN NULL;
END;
/

CREATE TABLE CLIENTS(
  ID NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY NOT NULL,
  NAME NVARCHAR2(255) NOT NULL,
  BALANCE NUMBER(*,2) DEFAULT 0 NOT NULL,
  IS_ACTIVE NUMBER(1) DEFAULT 0 NOT NULL,
  IS_PREPAY NUMBER(1) DEFAULT 0 NOT NULL
);

CREATE TABLE TRANSACTIONS(
  ID NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY NOT NULL,
  ID_CLIENT NUMBER NOT NULL,
  VALUE NUMBER(*,2) NOT NULL,
  TRANSACTION_TIME TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);